<script>
function showChapter(chapterNum) {
  // Hide all chapters
  const chapters = document.querySelectorAll('.chapter-section');
  chapters.forEach(chapter => {
    chapter.classList.remove('active');
  });
  
  // Show selected chapter
  const selectedChapter = document.getElementById('chapter-' + chapterNum);
  if (selectedChapter) {
    selectedChapter.classList.add('active');
  }
  
  // Update active link in chapter list
  const links = document.querySelectorAll('.chapter-list a');
  links.forEach(link => {
    link.classList.remove('active');
  });
  
  const activeLink = document.querySelector('a[href="#chapter-' + chapterNum + '"]');
  if (activeLink) {
    activeLink.classList.add('active');
  }
  
  // Update navigation buttons
  updateNavigation(chapterNum);
  
  // Scroll to top of content
  document.getElementById('book-content').scrollIntoView({ behavior: 'smooth' });
}

function updateNavigation(currentChapter) {
  const prevBtn = document.getElementById('prev-chapter');
  const nextBtn = document.getElementById('next-chapter');
  const currentChapterDisplay = document.getElementById('current-chapter');
  
  // Update current chapter display
  currentChapterDisplay.textContent = 'Chapter ' + currentChapter;
  
  // Update previous button
  if (currentChapter > 1) {
    prevBtn.style.display = 'inline-flex';
    prevBtn.onclick = () => showChapter(currentChapter - 1);
  } else {
    prevBtn.style.display = 'none';
  }
  
  // Update next button
  const totalChapters = document.querySelectorAll('.chapter-section').length;
  if (currentChapter < totalChapters) {
    nextBtn.style.display = 'inline-flex';
    nextBtn.onclick = () => showChapter(currentChapter + 1);
  } else {
    nextBtn.style.display = 'none';
  }
}

// Initialize on page load
document.addEventListener('DOMContentLoaded', function() {
  showChapter(1);
});
</script>

